import React from 'react';
import { withStyles, makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import {connect} from 'react-redux';
import Grid from '@material-ui/core/Grid';


const useStyles = makeStyles(theme => ({
    root: {
      width: '100%',
      marginTop: theme.spacing(3),
      overflowX: 'auto',
    },
    table: {
      minWidth: 700,
    },
  }));

  const StyledTableRow = withStyles(theme => ({
    root: {
      '&:nth-of-type(odd)': {
        backgroundColor: theme.palette.background.default,
      },
    },
  }))(TableRow);


  const StyledTableCell = withStyles(theme => ({
    head: {
      backgroundColor: theme.palette.common.black,
      color: theme.palette.common.white,
    },
    body: {
      fontSize: 14,
    },
  }))(TableCell);

function createData(first_name,last_name,age,gender,phone_number){

    return {first_name,last_name,age,gender,phone_number};

}

//const classes = useStyles();

export function table({data}){

    const classes = makeStyles(theme => ({
        root : {
            width : '100%',
            marginTop : theme.spacing(3),
            overflowX : 'auto'
        },
        table :{
            minWidth : 700,
        },
        tableCell: {
            paddingRight: 4,
            paddingLeft: 5
        }
    }))

    const rows = data.map(i => {
        return createData(i.first_name,i.last_name,i.age,i.gender,i.phone_number);
    })

    return(
        <React.Fragment styles={{width:'auto' , overflowX:'scroll'}} >
            <Grid xs={12}>

                <Table style={{width :'100px'}} >
                    <TableHead >
                        <TableRow >
                            <StyledTableCell className={classes.tableCell}>First_name</StyledTableCell>
                            <StyledTableCell className={classes.tableCell} align="right">Last_name</StyledTableCell>
                            <StyledTableCell className={classes.tableCell} align="right">Age</StyledTableCell>
                            <StyledTableCell className={classes.tableCell} align="right">Gender</StyledTableCell>
                            <StyledTableCell className={classes.tableCell} align="right">Phone_number</StyledTableCell>  
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {rows.map(i =>{
                            return(
                                <StyledTableRow key={i.phone_number}>
                                    <StyledTableCell align="right" className={classes.tableCell} component="th" scope="row">
                                        {i.first_name}
                                    </StyledTableCell>
                                    <StyledTableCell align="right" className={classes.tableCell}>
                                        {i.last_name}
                                    </StyledTableCell >
                                    <StyledTableCell align="right" className={classes.tableCell}>
                                        {i.age}
                                    </StyledTableCell>
                                    <StyledTableCell align="right" className={classes.tableCell} >
                                        {i.gender}
                                    </StyledTableCell>
                                    <StyledTableCell align="right" className={classes.tableCell}>
                                        {i.phone_number}
                                    </StyledTableCell>
                                </StyledTableRow>
                            )
                        })}
                    </TableBody>
                </Table>

            </Grid>
        </React.Fragment>
    );

}

const mapStateToProps = state => ({
    data : state.data
})

export default connect(mapStateToProps)(table);
